import { configureStore } from '@reduxjs/toolkit'
import searchReducer from './slices/searchSlice'
import sortReducer from './slices/sortSlice'


export const store = configureStore({
	reducer: {
		searcher: searchReducer,
		sorter: sortReducer
	},
})
