import React from 'react'
import { useSelector, useDispatch } from 'react-redux';
import axios from 'axios';
import qs from 'qs'
import { useNavigate } from 'react-router-dom';

import PizzaBlockSkeleton from '../components/PizzaBlock/PizzaBlockSkeleton';
import Categories from '../components/Categories/Categories';
import Sort from '../components/Sort/Sort';
import PizzaBlock from '../components/PizzaBlock/PizzaBlock';
import Pagination from '../components/Pagination/Pagination';
import { setFilters } from '../redux/slices/filterSlice';
import { listName } from '../components/Sort/Sort'



function Home() {

	const searchValue = useSelector(state => state.filterer.searchValue)
	const { categoryId, sortType, currentPage } = useSelector(state => state.filterer)
	const [items, setItems] = React.useState([])
	const [isLoading, setIsLoading] = React.useState(true)
	const dispatch = useDispatch()

	const skeletons = [... new Array(8)].map((_, index) => <PizzaBlockSkeleton key={index} />)
	const categories = ['Все пиццы', 'Мясные', 'Вегетарианские', 'Гриль', 'Острые', 'Cырные']
	const order = sortType.type.includes('-') ? 'asc' : 'desc'
	const sortBy = sortType.type.replace('-', '')
	const category = categoryId > 0 ? `category=${categoryId}` : ''
	const searchBy = searchValue ? `search=${searchValue}` : ''
	const navigate = useNavigate()
	const isMount = React.useRef(false)
	const isFetching = React.useRef(false)

	const fetching = () => {
		setIsLoading(true)
		axios.get(`https://62ed2633818ab252b60b026e.mockapi.io/items?${searchBy}&p=${currentPage}&l=8&${category}&sortBy=${sortBy}&order=${order}`)
			.then(res => {
				setItems(res.data)
				setIsLoading(false)
			})
	}

	const navigating = () => {
		const queryString = qs.stringify({
			searchValue,
			currentPage,
			categoryId,
			sortType: sortType.type,
		})
		navigate(`?${queryString}`)
	}

	const setterFilters = (locSearch) => {
		const params = qs.parse(locSearch.substring(1))
		const sort = listName.find(obj => obj.type === params.sortType)
		console.log(params.currentPage);
		dispatch(setFilters({
			...params,
			sortType: sort,
		}
		))
	}

	React.useEffect(() => {
		if (window.location.search)
			setterFilters(window.location.search)
	}, [])

	React.useEffect(() => {
		if (isFetching.current)
			fetching()
		window.scrollTo(0, 0)
		isFetching.current = true
	}, [categoryId, sortType, currentPage, searchValue])

	React.useEffect(() => {
		if (isMount.current)
			navigating()

		isMount.current = true;
	}, [categoryId, sortType, currentPage, searchValue])

	return (
		<div className='container'>
			<div className="content__top">
				<Categories />
				<Sort />
			</div>
			<h1 className="content__title">{categories[categoryId]}</h1>
			<div className="content__items">
				{isLoading
					? skeletons
					: items.map(item => <PizzaBlock key={item.id} {...item} />)}
			</div>
			<Pagination currentPage={currentPage} />
		</div>
	)
}

export default Home
