import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { onChangeCategoryId } from '../../redux/slices/filterSlice'


let categories = ['Все', 'Мясные', 'Вегетарианская', 'Гриль', 'Острые', 'Сырные']


function Categories() {

	const currentCategoryId = useSelector(state => state.filterer.categoryId)
	const dispatch = useDispatch()
	const setCurrentId = (index) => {
		dispatch(onChangeCategoryId(index))
	}

	return (
		<div className="categories">
			<ul>
				{categories.map((category, index) =>
					<li
						key={category}
						onClick={() => setCurrentId(index)}
						className={currentCategoryId === index ? 'active' : ''}>
						{category}
					</li>
				)}
			</ul>
		</div>
	)
}

export default Categories
