import { createSlice } from '@reduxjs/toolkit'

const initialState = {
	categoryId: 0,
	sortType: { name: 'популярные', type: 'raiting' },
	currentPage: 1
}

export const filterSlice = createSlice({
	name: 'filterer',
	initialState,
	reducers: {
		onChangeCategoryId: (state, action) => {
			state.categoryId = action.payload
		},
		onChangeSortType: (state, action) => {
			state.sortType = action.payload
		},
		onChangeCurrentPage: (state, action) => {
			state.currentPage = action.payload
		}
	},
})

export const { onChangeCategoryId, onChangeSortType, onChangeCurrentPage } = filterSlice.actions
export default filterSlice.reducer
