import { createSlice } from '@reduxjs/toolkit'

const initialState = {
	value: '',
}

export const searchSlice = createSlice({
	name: 'searcher',
	initialState,
	reducers: {
		onSearch: (state, action) => {
			state.value += action.payload
		}
	},
})

export const { onSearch } = searchSlice.actions

export default searchSlice.reducer
