import React from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { onChangeCategoryId } from '../../redux/slices/filterSlice'

function Categories({ onChangeCategoryId }) {

	const [activeIndex, setActiveIndex] = React.useState(0)
	const dispatch = useDispatch()
	const setActiveIndexAndCurrentId = (index) => {
		setActiveIndex(index)
		dispatch(onChangeCategoryId(index))
	}


	let categories = ['Все', 'Мясные', 'Вегетарианская', 'Гриль', 'Острые', 'Сырные']

	return (
		<div className="categories">
			<ul>
				{categories.map((category, index) =>
					<li
						key={category}
						onClick={() => setActiveIndexAndCurrentId(index)}
						className={activeIndex === index ? 'active' : ''}>
						{category}
					</li>
				)}
			</ul>
		</div>
	)
}

export default Categories
