import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { onChangeCategoryId, selectFilterByCategoryId } from '../../redux/slices/filterSlice'
import useWhyDidYouUpdate from 'ahooks/lib/useWhyDidYouUpdate';


const Categories: React.FC = () => {

	let categories = ['Все', 'Мясные', 'Вегетарианская', 'Гриль', 'Острые', 'Сырные']
	const currentCategoryId = useSelector(selectFilterByCategoryId)
	const dispatch = useDispatch()
	const setCurrentId = (index: number) => dispatch(onChangeCategoryId(index))
	useWhyDidYouUpdate('Categories', currentCategoryId)
	return (
		<div className="categories">
			<ul>
				{categories.map((category, index) =>
					<li
						key={category}
						onClick={() => setCurrentId(index)}
						className={currentCategoryId === index ? 'active' : ''}>
						{category}
					</li>
				)}
			</ul>
		</div>
	)
}

export default Categories
