import React from 'react'

import PizzaBlockSkeleton from '../components/PizzaBlock/PizzaBlockSkeleton';
import Categories from '../components/Categories/Categories';
import Sort from '../components/Sort/Sort';
import PizzaBlock from '../components/PizzaBlock/PizzaBlock';


function Home({ searchValue, setSearchValue }) {
	const categories = ['Все пиццы', 'Мясные', 'Вегетарианские', 'Гриль', 'Острые', 'Закрытые']
	const [items, setItems] = React.useState([])
	const [isLoading, setIsLoading] = React.useState(true)
	const [categoryId, setCategoryId] = React.useState(0)
	const [sortType, setSortType] = React.useState({ name: 'популярные', type: 'raiting' })

	const sortBy = sortType.type.replace('-', '')
	const order = sortType.type.includes('-') ? 'asc' : 'desc'
	const category = categoryId > 0 ? `category=${categoryId}` : ''

	React.useEffect(() => {
		setIsLoading(true)
		fetch(`https://62ed2633818ab252b60b026e.mockapi.io/items?${category}&sortBy=${sortBy}&order=${order}`)
			.then(res => res.json())
			.then(items => {
				setItems(items)
				setIsLoading(false)
			})
		window.scrollTo(0, 0)
	}, [categoryId, sortType])

	React.useEffect(() => {
		items.filter(item => item.name == searchValue)
	}, [searchValue])

	return (
		<div className='container'>
			<div className="content__top">
				<Categories
					categoryId={categoryId}
					onChangeCategoryId={setCategoryId}
				/>
				<Sort
					sortType={sortType}
					onChangeSortType={setSortType}
				/>
			</div>
			<h1 className="content__title">{categories[categoryId]}</h1>
			<div className="content__items">
				{isLoading
					? [... new Array(8)].map((_, index) => <PizzaBlockSkeleton key={index} />)
					: items.map(item => <PizzaBlock key={item.id} {...item} />)
				}
			</div>
		</div>
	)
}

export default Home
