import { createSlice } from '@reduxjs/toolkit'

const initialState = {
	value: '1',
}

export const searchSlice = createSlice({
	name: 'searcher',
	initialState,
	reducers: {
		onSearch: (state) => {
			console.log(state.value);
		}
	},
})

export const { onSearch } = searchSlice.actions

export default searchSlice.reducer
