import { createAsyncThunk, createSlice } from '@reduxjs/toolkit'
import axios from 'axios'

const fetchPizzas = createAsyncThunk('pizza/fetchPizzas', async (params) => {
	const { data } = await axios.get(`https://62ed2633818ab252b60b026e.mockapi.io/items?${searchBy}&p=${currentPage}&l=8&${category}&sortBy=${sortBy}&order=${order}`)
	return data
})

const initialState = {
	items: [],
}

const pizzaSlice = createSlice({
	name: 'pizza',
	initialState,
	reducers: {
		setItems(state, action) {
			state.items = action.payload
		}
	},
	extraReducers: (builder) => {
		builder.addCase(fetchPizzas.fulfilled, (state, action) => {
			state.items
		})
	}
})

export const { setItems } = pizzaSlice.actions

export default pizzaSlice.reducer
