import { createSlice } from '@reduxjs/toolkit'

const initialState = {
	value: 0,
}

export const filterSlice = createSlice({
	name: 'filterer',
	initialState,

	reducers: {
		onChangeCategoryId: (state, action) => {
			state.value = action.payload
		},
	},
})

export const { onChangeCategoryId } = filterSlice.actions

export default filterSlice.reducer
