// import pizzas from './assets/db.json'

const pizza = []

function App() {
	//https://62ed2633818ab252b60b026e.mockapi.io/items

	const pizas2 = fetch('https://62ed2633818ab252b60b026e.mockapi.io/items').then(res => {
		return res.json()
	}).then(json => {
		return json
	})
	return (
		<div className="wrapper">
			<Header />
			<div className="content">
				<div className="container">
					<div className="content__top">
						<Categories />
						<Sort />
					</div>
					<h2 className="content__title">Все пиццы</h2>
					<div className="content__items">
						{pizzas.map(pizza =>
							<PizzaItem key={pizza.id} {...pizza} />)}
					</div>
				</div>
			</div>
		</div>
	);
}

export default App;
