import { createSlice } from '@reduxjs/toolkit'

const initialState = {
	value: true,
}

const popupSlice = createSlice({
	name: 'popup',
	initialState,
	reducers: {
		switchVisible(state) {
			state.value = !state.value
		}
	}
})

export const { switchVisible } = popupSlice.actions

export default popupSlice.reducer
