import { createSlice } from '@reduxjs/toolkit'

type CartItem = {
	id: number,
	name: string,
	imgUrl: string,
	price: number,
	type: number,
	size: number,
}

interface CartSliseState {
	totalPrice: number;
	totalCount: number;
	items: CartItem[]
}
const initialState: CartSliseState = {
	totalPrice: 0,
	totalCount: 0,
	items: [],
}

const cartSlice = createSlice({
	name: 'cart',
	initialState,
	reducers: {
		addItem: (state, action) => {
			const findItem = state.items.find(obj => obj.id === action.payload.id)
			if (findItem) {
				findItem.count++
			} else {
				state.items.push({
					...action.payload,
					count: 1,
				})
			}
			state.totalPrice = state.items.reduce((sum, curr) => {
				return (curr.price * curr.count) + sum
			}, 0)
			state.totalCount = state.items.reduce((sum, curItem) => sum + curItem.count, 0)
		},

		removeItem: (state, action) => {
			const findItem = state.items.find(obj => obj.id === action.payload.id)
			if (findItem) {
				findItem.count--
			}

			state.items = state.items.filter(object => object.id !== action.payload)
			state.totalPrice = state.items.reduce((sum, curr) => {
				return (curr.price * curr.count) + sum
			}, 0)
			state.totalCount = state.items.reduce((sum, curItem) => sum + curItem.count, 0)
		},

		clearItems: (state) => {
			state.items = []
			state.totalPrice = 0
		},

	},
})

export const selectorCart = (state) => state.cart
export const selectorCountItem = (id) => state => state.cart.items.find(obj => obj.id === id)

export const { addItem, removeItem, clearItems } = cartSlice.actions

export default cartSlice.reducer
