import React from 'react'
import cl from './Pagination.module.scss'

function Pagination({ items }) {

	const countItems = items.length

	const countPages = countItems / 8

	return (
		<div className={cl.root}>
			<ul>
				{countPages}
			</ul>
		</div>
	)
}

export default Pagination
