import React from 'react'

import PizzaBlockSkeleton from '../components/PizzaBlock/PizzaBlockSkeleton';
import Categories from '../components/Categories/Categories';
import Sort from '../components/Sort/Sort';
import PizzaBlock from '../components/PizzaBlock/PizzaBlock';
import Pagination from '../components/Pagination/Pagination';
import { useSelector, useDispatch } from 'react-redux';


function Home() {
	const searchValue = useSelector(state => state.searcher.value)
	const categoryId = useSelector(state => state.filterer.value)
	const sortType = useSelector(state => state.sorter.value)

	const categories = ['Все пиццы', 'Мясные', 'Вегетарианские', 'Гриль', 'Острые', 'Cырные']

	const [items, setItems] = React.useState([])
	const [isLoading, setIsLoading] = React.useState(true)
	const [currentPage, setCurrentPage] = React.useState(1);

	const order = sortType.type.includes('-') ? 'asc' : 'desc'
	const sortBy = sortType.type.replace('-', '')
	const category = categoryId > 0 ? `category=${categoryId}` : ''

	React.useEffect(() => {
		setIsLoading(true)
		fetch(`https://62ed2633818ab252b60b026e.mockapi.io/items?p=${currentPage}&l=8&${category}&sortBy=${sortBy}&order=${order}`)
			.then(res => res.json())
			.then(items => {
				setItems(items)
				setIsLoading(false)
			})
		window.scrollTo(0, 0)
	}, [categoryId, sortType, currentPage])

	const pizzas = searchValue
		? items.filter(obj => {
			if (obj.name.toLowerCase().includes(searchValue.toLowerCase())) {
				return true
			}
			return false
		})
		: items
	const skeletons = [... new Array(8)].map((_, index) => <PizzaBlockSkeleton key={index} />)

	return (
		<div className='container'>
			<div className="content__top">
				<Categories />
				<Sort />
			</div>
			<h1 className="content__title">{categories[categoryId]}</h1>
			<div className="content__items">
				{isLoading
					? skeletons
					: pizzas.map(item => <PizzaBlock key={item.id} {...item} />)}
			</div>
			<Pagination
				items={items}
				setCurrentPage={setCurrentPage}
			/>
		</div>
	)
}

export default Home
